import { useState, useRef, useCallback } from "react";
import { supabase } from "@/lib/supabase";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { Paperclip, X, FileText, Image, File, Upload, Loader2 } from "lucide-react";
import type { EventAttachment, EventAttachmentInsert } from "@/lib/supabase-types";

const STORAGE_BUCKET = "event-attachments";
const MAX_FILE_SIZE = 10 * 1024 * 1024;

interface AttachmentUploadProps {
  eventId: string;
  attachments: EventAttachment[];
  onAttachmentsChange: (attachments: EventAttachment[]) => void;
  disabled?: boolean;
}

function getFileIcon(mimeType: string | null) {
  if (!mimeType) return File;
  if (mimeType.startsWith("image/")) return Image;
  if (mimeType.includes("pdf") || mimeType.includes("document") || mimeType.includes("text")) return FileText;
  return File;
}

function formatFileSize(bytes: number | null): string {
  if (!bytes) return "Unknown size";
  if (bytes < 1024) return `${bytes} B`;
  if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(1)} KB`;
  return `${(bytes / (1024 * 1024)).toFixed(1)} MB`;
}

export function AttachmentUpload({ 
  eventId, 
  attachments, 
  onAttachmentsChange,
  disabled = false 
}: AttachmentUploadProps) {
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleFileSelect = useCallback(async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;

    const file = files[0];

    if (file.size > MAX_FILE_SIZE) {
      toast({
        title: "File too large",
        description: "Maximum file size is 10MB",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);
    setUploadProgress(`Uploading ${file.name}...`);

    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${eventId}/${Date.now()}-${Math.random().toString(36).substring(7)}.${fileExt}`;

      const { error: uploadError } = await supabase.storage
        .from(STORAGE_BUCKET)
        .upload(fileName, file, {
          cacheControl: '3600',
          upsert: false
        });

      if (uploadError) {
        if (uploadError.message.includes("bucket") || uploadError.message.includes("not found")) {
          throw new Error("Storage bucket not configured. Please contact support.");
        }
        throw uploadError;
      }

      const attachmentData: EventAttachmentInsert = {
        event_id: eventId,
        file_name: file.name,
        storage_path: fileName,
        storage_bucket: STORAGE_BUCKET,
        file_size_bytes: file.size,
        mime_type: file.type || null,
      };

      const { data: newAttachment, error: insertError } = await supabase
        .from("event_attachments")
        .insert(attachmentData)
        .select()
        .single();

      if (insertError) throw insertError;

      onAttachmentsChange([...attachments, newAttachment]);

      toast({
        title: "File uploaded",
        description: `${file.name} has been attached to this event.`,
      });
    } catch (error: any) {
      console.error("Upload error:", error);
      toast({
        title: "Upload failed",
        description: error.message || "Failed to upload file",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
      setUploadProgress(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  }, [eventId, attachments, onAttachmentsChange, toast]);

  const handleDelete = useCallback(async (attachment: EventAttachment) => {
    try {
      const { error: storageError } = await supabase.storage
        .from(attachment.storage_bucket)
        .remove([attachment.storage_path]);

      if (storageError) {
        console.warn("Storage deletion warning:", storageError);
      }

      const { error: dbError } = await supabase
        .from("event_attachments")
        .delete()
        .eq("id", attachment.id);

      if (dbError) throw dbError;

      onAttachmentsChange(attachments.filter(a => a.id !== attachment.id));

      toast({
        title: "Attachment removed",
        description: `${attachment.file_name} has been deleted.`,
      });
    } catch (error: any) {
      console.error("Delete error:", error);
      toast({
        title: "Delete failed",
        description: error.message || "Failed to delete attachment",
        variant: "destructive",
      });
    }
  }, [attachments, onAttachmentsChange, toast]);

  const handleDownload = useCallback(async (attachment: EventAttachment) => {
    try {
      const { data, error } = await supabase.storage
        .from(attachment.storage_bucket)
        .download(attachment.storage_path);

      if (error) throw error;

      const url = URL.createObjectURL(data);
      const a = document.createElement('a');
      a.href = url;
      a.download = attachment.file_name;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    } catch (error: any) {
      console.error("Download error:", error);
      toast({
        title: "Download failed",
        description: error.message || "Failed to download file",
        variant: "destructive",
      });
    }
  }, [toast]);

  return (
    <div className="space-y-3">
      <div className="flex items-center gap-2">
        <Paperclip className="h-4 w-4 text-muted-foreground" />
        <span className="text-sm font-medium">Attachments</span>
      </div>

      {attachments.length > 0 && (
        <div className="space-y-2">
          {attachments.map((attachment) => {
            const IconComponent = getFileIcon(attachment.mime_type);
            return (
              <div
                key={attachment.id}
                className="flex items-center gap-2 p-2 rounded-md bg-muted/50 group"
              >
                <IconComponent className="h-4 w-4 text-muted-foreground flex-shrink-0" />
                <button
                  type="button"
                  onClick={() => handleDownload(attachment)}
                  className="flex-1 text-left text-sm truncate hover:underline"
                  data-testid={`attachment-download-${attachment.id}`}
                >
                  {attachment.file_name}
                </button>
                <span className="text-xs text-muted-foreground flex-shrink-0">
                  {formatFileSize(attachment.file_size_bytes)}
                </span>
                {!disabled && (
                  <Button
                    type="button"
                    variant="ghost"
                    size="icon"
                    className="h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity"
                    onClick={() => handleDelete(attachment)}
                    data-testid={`button-delete-attachment-${attachment.id}`}
                  >
                    <X className="h-3 w-3" />
                  </Button>
                )}
              </div>
            );
          })}
        </div>
      )}

      {!disabled && (
        <div>
          <input
            ref={fileInputRef}
            type="file"
            className="hidden"
            onChange={handleFileSelect}
            disabled={isUploading}
            data-testid="input-attachment-file"
          />
          <Button
            type="button"
            variant="outline"
            size="sm"
            onClick={() => fileInputRef.current?.click()}
            disabled={isUploading}
            data-testid="button-add-attachment"
          >
            {isUploading ? (
              <>
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                {uploadProgress || "Uploading..."}
              </>
            ) : (
              <>
                <Upload className="h-4 w-4 mr-2" />
                Add attachment
              </>
            )}
          </Button>
          <p className="text-xs text-muted-foreground mt-1">
            Max file size: 10MB
          </p>
        </div>
      )}

      {attachments.length === 0 && disabled && (
        <p className="text-sm text-muted-foreground">No attachments</p>
      )}
    </div>
  );
}

client/src/pages/shared-event.tsx.content:
import { useState, useEffect } from "react";
import { useParams } from "wouter";
import { format, parseISO } from "date-fns";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Calendar,
  Clock,
  MapPin,
  Video,
  ExternalLink,
  CalendarCheck,
  User,
  AlertCircle
} from "lucide-react";

interface SharedEventData {
  event: {
    title: string;
    description: string | null;
    start_time: string;
    end_time: string;
    all_day: boolean;
    location: string | null;
    event_type: string;
    color: string | null;
  };
  meeting: {
    daily_room_url: string;
    title: string;
    scheduled_start: string;
    scheduled_end: string;
  } | null;
  hostName: string | null;
}

export default function SharedEventPage() {
  const { token } = useParams<{ token: string }>();
  const [data, setData] = useState<SharedEventData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchSharedEvent() {
      if (!token) {
        setError("Invalid share link");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`/api/shared-event/${token}`);

        if (response.status === 404) {
          setError("Event not found or link has expired");
          setLoading(false);
          return;
        }

        if (response.status === 410) {
          setError("This share link has expired");
          setLoading(false);
          return;
        }

        if (!response.ok) {
          const errorData = await response.json();
          setError(errorData.error || "Failed to load event");
          setLoading(false);
          return;
        }

        const eventData = await response.json();
        setData(eventData);
      } catch (err) {
        console.error("Error fetching shared event:", err);
        setError("Failed to load event");
      } finally {
        setLoading(false);
      }
    }

    fetchSharedEvent();
  }, [token]);

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="w-full max-w-lg">
          <CardHeader>
            <Skeleton className="h-8 w-3/4" />
            <Skeleton className="h-4 w-1/2 mt-2" />
          </CardHeader>
          <CardContent className="space-y-4">
            <Skeleton className="h-4 w-full" />
            <Skeleton className="h-4 w-full" />
            <Skeleton className="h-4 w-2/3" />
          </CardContent>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="w-full max-w-lg">
          <CardHeader>
            <div className="flex items-center gap-2 text-destructive">
              <AlertCircle className="h-6 w-6" />
              <CardTitle>Event Not Available</CardTitle>
            </div>
            <CardDescription>{error}</CardDescription>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground">
              The event you're looking for might have been removed, the link may have expired, 
              or you may not have permission to view it.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!data) return null;

  const { event, meeting, hostName } = data;
  const startDate = parseISO(event.start_time);
  const endDate = parseISO(event.end_time);
  const isMultiDay = format(startDate, "yyyy-MM-dd") !== format(endDate, "yyyy-MM-dd");

  const eventTypeColors: Record<string, string> = {
    meeting: "bg-blue-500",
    task: "bg-green-500",
    personal: "bg-purple-500",
    reminder: "bg-yellow-500",
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <Card className="w-full max-w-lg">
        <CardHeader>
          <div className="flex items-center gap-2 mb-2">
            <Badge 
              className={`${eventTypeColors[event.event_type] || "bg-gray-500"} text-white`}
              data-testid="badge-event-type"
            >
              {event.event_type.charAt(0).toUpperCase() + event.event_type.slice(1)}
            </Badge>
            {event.all_day && (
              <Badge variant="outline" data-testid="badge-all-day">All Day</Badge>
            )}
          </div>
          <CardTitle className="text-2xl" data-testid="text-event-title">{event.title}</CardTitle>
          {hostName && (
            <CardDescription className="flex items-center gap-2">
              <User className="h-4 w-4" />
              Hosted by {hostName}
            </CardDescription>
          )}
        </CardHeader>

        <CardContent className="space-y-6">
          <div className="space-y-3">
            <div className="flex items-start gap-3">
              <Calendar className="h-5 w-5 text-muted-foreground mt-0.5" />
              <div>
                <p className="font-medium" data-testid="text-event-date">
                  {event.all_day ? (
                    isMultiDay ? (
                      `${format(startDate, "EEEE, MMMM d")} - ${format(endDate, "EEEE, MMMM d, yyyy")}`
                    ) : (
                      format(startDate, "EEEE, MMMM d, yyyy")
                    )
                  ) : (
                    format(startDate, "EEEE, MMMM d, yyyy")
                  )}
                </p>
                {!event.all_day && (
                  <p className="text-sm text-muted-foreground flex items-center gap-1" data-testid="text-event-time">
                    <Clock className="h-4 w-4" />
                    {format(startDate, "h:mm a")} - {format(endDate, "h:mm a")}
                    {isMultiDay && ` (${format(endDate, "MMM d")})`}
                  </p>
                )}
              </div>
            </div>

            {event.location && (
              <div className="flex items-start gap-3">
                <MapPin className="h-5 w-5 text-muted-foreground mt-0.5" />
                <p data-testid="text-event-location">{event.location}</p>
              </div>
            )}
          </div>

          {event.description && (
            <>
              <Separator />
              <div>
                <h3 className="font-medium mb-2">Description</h3>
                <p className="text-sm text-muted-foreground whitespace-pre-wrap" data-testid="text-event-description">
                  {event.description}
                </p>
              </div>
            </>
          )}

          {meeting && (
            <>
              <Separator />
              <div className="space-y-3">
                <div className="flex items-center gap-2">
                  <Video className="h-5 w-5 text-muted-foreground" />
                  <h3 className="font-medium">Video Call</h3>
                </div>
                <div className="bg-muted/50 rounded-lg p-4">
                  <p className="text-sm text-muted-foreground mb-3">
                    Join the video call when the event starts
                  </p>
                  <Button asChild className="w-full" data-testid="button-join-call">
                    <a
                      href={meeting.daily_room_url}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      <ExternalLink className="h-4 w-4 mr-2" />
                      Join Video Call
                    </a>
                  </Button>
                </div>
              </div>
            </>
          )}

          <Separator />

          <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground">
            <CalendarCheck className="h-4 w-4" />
            <span>Shared via Desk Mate</span>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

