    if (parseFloat(journalStats.avgMoodScore) >= 3.5) {
      score += 10;
    } else if (parseFloat(journalStats.avgMoodScore) >= 3) {
      score += 5;
    }

    if (calendarStats.totalEvents > 0) {
      score += 10;
    }

    if (meetingStats.completedMeetings > meetingStats.cancelledMeetings) {
