import { useState, useRef, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useMutation, useQuery } from "@tanstack/react-query";
import {
  Sheet,
  SheetContent,
  SheetHeader,
  SheetTitle,
  SheetDescription,
} from "@/components/ui/sheet";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import {
  Bot,
  Send,
  Loader2,
  Sparkles,
  Calendar,
  BookOpen,
  Video,
  BarChart3,
  MessageSquare,
  ArrowRight,
} from "lucide-react";
import { cn } from "@/lib/utils";
import { useAuth } from "@/contexts/AuthContext";
import { fetchWithAuth } from "@/lib/fetchWithAuth";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
  created_at: string;
}

interface AISettings {
  context_calendar: boolean;
  context_journal: boolean;
  context_meetings: boolean;
  context_analytics: boolean;
  preferred_language: string;
  summary_style: string;
}

interface AISidebarProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

interface ChatResponse {
  conversationId?: string;
  userMessage?: Message;
  assistantMessage?: Message;
  response?: string;
  contextUsed?: object;
}

export function AISidebar({ open, onOpenChange }: AISidebarProps) {
  const { t } = useTranslation();
  const { user } = useAuth();
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState<Message[]>([]);
  const [conversationId, setConversationId] = useState<string | null>(null);
  const scrollAreaRef = useRef<HTMLDivElement>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const { data: settings } = useQuery<AISettings>({
    queryKey: ["/api/ai/settings"],
    enabled: !!user,
  });

  const chatMutation = useMutation({
    mutationFn: async (userMessage: string): Promise<ChatResponse> => {
      let response: Response;
      if (conversationId) {
        response = await fetchWithAuth(`/api/ai/conversations/${conversationId}/messages`, {
          method: "POST",
          body: JSON.stringify({ message: userMessage }),
        });
      } else {
        response = await fetchWithAuth("/api/ai/chat", {
          method: "POST",
          body: JSON.stringify({ message: userMessage, saveToConversation: true }),
        });
      }
      if (!response.ok) {
        throw new Error("Failed to send message");
      }
      return response.json();
    },
    onSuccess: (data: ChatResponse) => {
      if (data.conversationId && !conversationId) {
        setConversationId(data.conversationId);
      }

      if (data.userMessage && data.assistantMessage) {
        setMessages((prev) => [
          ...prev,
          data.userMessage!,
          data.assistantMessage!,
        ]);
      } else if (data.response) {
        setMessages((prev) => [
          ...prev,
          {
            id: `temp-${Date.now()}`,
            role: "assistant" as const,
            content: data.response || "",
            created_at: new Date().toISOString(),
          },
        ]);
      }
    },
  });

  const handleSendMessage = async () => {
    if (!message.trim() || chatMutation.isPending) return;

    const userMsg: Message = {
      id: `temp-user-${Date.now()}`,
      role: "user",
      content: message.trim(),
      created_at: new Date().toISOString(),
    };

    setMessages((prev) => [...prev, userMsg]);
    const currentMessage = message;
    setMessage("");

    try {
      await chatMutation.mutateAsync(currentMessage);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleNewChat = () => {
    setMessages([]);
    setConversationId(null);
  };

  useEffect(() => {
    if (scrollAreaRef.current) {
      scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    if (open && textareaRef.current) {
      textareaRef.current.focus();
    }
  }, [open]);

  const contextItems = [
    { key: "calendar", icon: Calendar, label: t("ai.context.calendar", "Calendar"), enabled: settings?.context_calendar },
    { key: "journal", icon: BookOpen, label: t("ai.context.journal", "Journal"), enabled: settings?.context_journal },
    { key: "meetings", icon: Video, label: t("ai.context.meetings", "Meetings"), enabled: settings?.context_meetings },
    { key: "analytics", icon: BarChart3, label: t("ai.context.analytics", "Analytics"), enabled: settings?.context_analytics },
  ];

  const quickActions = [
    { label: t("ai.quick.todaySchedule", "What's my schedule for today?"), icon: Calendar },
    { label: t("ai.quick.moodTrend", "How has my mood been this week?"), icon: BookOpen },
    { label: t("ai.quick.upcomingMeetings", "What meetings do I have coming up?"), icon: Video },
    { label: t("ai.quick.productivityTips", "Any productivity insights for me?"), icon: Sparkles },
  ];

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent
        side="right"
        className="w-full sm:max-w-md p-0 flex flex-col"
        data-testid="ai-sidebar"
      >
        <SheetHeader className="px-4 py-3 border-b flex-shrink-0">
          <div className="flex items-center justify-between gap-2">
            <div className="flex items-center gap-2">
              <div className="p-1.5 bg-primary/10 rounded-lg">
                <Bot className="h-5 w-5 text-primary" />
              </div>
              <div>
                <SheetTitle className="text-base">
                  {t("ai.title", "Desk Mate AI")}
                </SheetTitle>
                <SheetDescription className="text-xs">
                  {t("ai.subtitle", "Your productivity assistant")}
                </SheetDescription>
              </div>
            </div>
            {messages.length > 0 && (
              <Button
                variant="ghost"
                size="sm"
                onClick={handleNewChat}
                data-testid="button-new-chat"
              >
                <MessageSquare className="h-4 w-4 mr-1" />
                {t("ai.newChat", "New")}
              </Button>
            )}
          </div>
        </SheetHeader>

        <div className="flex-1 overflow-hidden flex flex-col">
          {messages.length === 0 ? (
            <div className="flex-1 p-4 space-y-4 overflow-y-auto">
              <div className="text-center py-6">
                <div className="mx-auto w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center mb-3">
                  <Sparkles className="h-6 w-6 text-primary" />
                </div>
                <h3 className="font-semibold text-lg mb-1">
                  {t("ai.welcome.title", "How can I help you today?")}
                </h3>
                <p className="text-sm text-muted-foreground">
                  {t("ai.welcome.subtitle", "I have access to your calendar, journal, and meeting data.")}
                </p>
              </div>

              <div className="space-y-2">
                <p className="text-xs font-medium text-muted-foreground uppercase tracking-wide px-1">
                  {t("ai.quickActions", "Quick Actions")}
                </p>
                <div className="space-y-1">
                  {quickActions.map((action, index) => (
                    <button
                      key={index}
                      onClick={() => {
                        setMessage(action.label);
                        textareaRef.current?.focus();
                      }}
                      className="w-full flex items-center gap-3 p-3 text-left text-sm rounded-lg hover-elevate bg-muted/50 transition-colors"
                      data-testid={`button-quick-action-${index}`}
                    >
                      <action.icon className="h-4 w-4 text-muted-foreground flex-shrink-0" />
                      <span>{action.label}</span>
                      <ArrowRight className="h-3 w-3 ml-auto text-muted-foreground" />
                    </button>
                  ))}
                </div>
              </div>

              <Separator className="my-4" />

              <div className="space-y-2">
                <p className="text-xs font-medium text-muted-foreground uppercase tracking-wide px-1">
                  {t("ai.contextSources", "Active Context")}
                </p>
                <div className="flex flex-wrap gap-2">
                  {contextItems.map((item) => (
                    <Badge
                      key={item.key}
                      variant={item.enabled ? "default" : "outline"}
                      className={cn(
                        "gap-1",
                        !item.enabled && "opacity-50"
                      )}
                    >
                      <item.icon className="h-3 w-3" />
                      {item.label}
                    </Badge>
                  ))}
                </div>
                <p className="text-xs text-muted-foreground">
                  {t("ai.contextHint", "Configure in Profile settings")}
                </p>
              </div>
            </div>
          ) : (
            <ScrollArea className="flex-1 p-4" ref={scrollAreaRef}>
              <div className="space-y-4">
                {messages.map((msg, index) => (
                  <div
                    key={msg.id || index}
                    className={cn(
                      "flex gap-3",
                      msg.role === "user" ? "justify-end" : "justify-start"
                    )}
                  >
                    {msg.role === "assistant" && (
                      <div className="flex-shrink-0 w-7 h-7 rounded-full bg-primary/10 flex items-center justify-center">
                        <Bot className="h-4 w-4 text-primary" />
                      </div>
                    )}
                    <div
                      className={cn(
                        "max-w-[85%] rounded-lg px-3 py-2 text-sm",
                        msg.role === "user"
                          ? "bg-primary text-primary-foreground"
                          : "bg-muted"
                      )}
                      data-testid={`message-${msg.role}-${index}`}
                    >
                      <p className="whitespace-pre-wrap">{msg.content}</p>
                    </div>
                  </div>
                ))}
                {chatMutation.isPending && (
                  <div className="flex gap-3 justify-start">
                    <div className="flex-shrink-0 w-7 h-7 rounded-full bg-primary/10 flex items-center justify-center">
                      <Bot className="h-4 w-4 text-primary" />
                    </div>
                    <div className="bg-muted rounded-lg px-3 py-2">
                      <Loader2 className="h-4 w-4 animate-spin" />
                    </div>
                  </div>
                )}
              </div>
            </ScrollArea>
          )}

          <div className="flex-shrink-0 p-4 border-t bg-background">
            <div className="flex gap-2">
              <Textarea
                ref={textareaRef}
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder={t("ai.inputPlaceholder", "Ask me anything...")}
                className="min-h-[44px] max-h-32 resize-none"
                rows={1}
                disabled={chatMutation.isPending}
                data-testid="input-ai-message"
              />
              <Button
                size="icon"
                onClick={handleSendMessage}
                disabled={!message.trim() || chatMutation.isPending}
                data-testid="button-send-ai-message"
              >
                {chatMutation.isPending ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <Send className="h-4 w-4" />
                )}
              </Button>
            </div>
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
}

export function AITriggerButton({
  onClick,
  className,
}: {
  onClick: () => void;
  className?: string;
}) {
  const { t } = useTranslation();

  return (
    <Button
      variant="outline"
      size="icon"
      onClick={onClick}
      className={cn("relative", className)}
      data-testid="button-open-ai-sidebar"
    >
      <Bot className="h-4 w-4" />
      <span className="absolute -top-1 -right-1 w-2 h-2 bg-primary rounded-full animate-pulse" />
      <span className="sr-only">{t("ai.openAssistant", "Open AI Assistant")}</span>
    </Button>
  );
}

client/src/lib/fetchWithAuth.ts.content:
import { supabase } from "@/lib/supabase";

export async function fetchWithAuth(url: string, options: RequestInit = {}): Promise<Response> {
  const { data: { session } } = await supabase.auth.getSession();
  if (!session) {
    console.error("[fetchWithAuth] No session available");
    throw new Error("Not authenticated");
  }

  try {
    const response = await fetch(url, {
      ...options,
      headers: {
        ...options.headers,
        Authorization: `Bearer ${session.access_token}`,
        "Content-Type": "application/json",
      },
    });
    return response;
  } catch (error) {
    console.error("[fetchWithAuth] Fetch error:", error);
    throw error;
  }
}

server/ai-service.ts.content:
import OpenAI from "openai";
import { createClient } from "@supabase/supabase-js";
import fs from "fs";
import path from "path";

// the newest OpenAI model is "gpt-4o-mini" as requested by the user
// Using user's own API key for GPT-4o-mini and Whisper
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

function getServiceRoleClient() {
  const supabaseUrl = process.env.VITE_SUPABASE_URL;
  const serviceRoleKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

  if (!supabaseUrl || !serviceRoleKey) {
    throw new Error("Missing Supabase URL or service role key");
  }

  return createClient(supabaseUrl, serviceRoleKey);
}

// Types for AI features
export interface AIConversation {
  id: string;
  user_id: string;
  title: string | null;
  context_sources: {
    calendar: boolean;
    journal: boolean;
    meetings: boolean;
    analytics: boolean;
  };
  created_at: string;
  updated_at: string;
}

export interface AIMessage {
  id: string;
  conversation_id: string;
  role: "user" | "assistant" | "system";
  content: string;
  context_used: object | null;
  created_at: string;
}

export interface AISettings {
  id: string;
  user_id: string;
  context_calendar: boolean;
  context_journal: boolean;
  context_meetings: boolean;
  context_analytics: boolean;
  preferred_language: string;
  summary_style: "concise" | "detailed" | "bullet_points";
  created_at: string;
  updated_at: string;
}

// Transcription interface matching existing transcriptions table
// Note: transcriptions table uses recording_id -> meeting_recordings -> meetings
export interface Transcription {
  id: string;
  recording_id: string;
  transcript_text: string;
  transcript_json: object | null;
  language: string | null;
  word_count: number | null;
  confidence_score: number | null;
  whisper_model_used: string;
  processing_time_seconds: number | null;
  created_at: string;
  updated_at: string;
}

// Meeting summary interface matching existing meeting_summaries table
export interface MeetingSummary {
  id: string;
  meeting_id: string;
  transcription_id: string | null;
  summary_text: string;
  key_points: string[] | null;
  action_items: string[] | null;
  topics_discussed: string[] | null;
  sentiment_analysis: object | null;
  participants_mentioned: string[] | null;
  gpt_model_used: string;
  tokens_used: number | null;
  processing_cost_usd: number | null;
  created_at: string;
  updated_at: string;
}

// For backward compatibility with existing code
export interface MeetingTranscription {
  id: string;
  meeting_id: string;
  transcription: string | null;
  status: string;
}

// Context gathering for AI chat
export interface UserContext {
  calendar_events?: any[];
  journal_entries?: any[];
  meetings?: any[];
  analytics?: {
    mood_average?: number;
    total_events?: number;
    total_meetings?: number;
    journal_count?: number;
  };
}

// Get user's AI settings
export async function getAISettings(userId: string): Promise<AISettings | null> {
  const serviceClient = getServiceRoleClient();

  const { data, error } = await serviceClient
    .from("ai_settings")
    .select("*")
    .eq("user_id", userId)
    .single();

  if (error && error.code !== "PGRST116") {
    console.error("Error fetching AI settings:", error);
    return null;
  }

  return data;
}

// Create or update AI settings
export async function upsertAISettings(
  userId: string,
  settings: Partial<AISettings>
): Promise<AISettings | null> {
  const serviceClient = getServiceRoleClient();

  const { data, error } = await serviceClient
    .from("ai_settings")
    .upsert({
      user_id: userId,
      ...settings,
      updated_at: new Date().toISOString(),
    }, {
      onConflict: "user_id",
    })
    .select()
    .single();

  if (error) {
    console.error("Error upserting AI settings:", error);
    return null;
  }

  return data;
}

// Gather context based on user settings
export async function gatherUserContext(
  userId: string,
  settings: AISettings | null
): Promise<UserContext> {
  const serviceClient = getServiceRoleClient();
  const context: UserContext = {};

  const effectiveSettings = settings || {
    context_calendar: true,
    context_journal: true,
    context_meetings: true,
    context_analytics: true,
  };

  // Get date range - last 30 days
  const endDate = new Date();
  const startDate = new Date();
  startDate.setDate(startDate.getDate() - 30);

  // Gather calendar events
  if (effectiveSettings.context_calendar) {
    const { data: events } = await serviceClient
      .from("calendar_events")
      .select("id, title, description, start_time, end_time, event_type, location")
      .eq("user_id", userId)
      .eq("is_deleted", false)
      .gte("start_time", startDate.toISOString())
      .lte("start_time", endDate.toISOString())
      .order("start_time", { ascending: false })
      .limit(50);

    context.calendar_events = events || [];
  }

  // Gather journal entries
  if (effectiveSettings.context_journal) {
    const { data: entries } = await serviceClient
      .from("journal_entries")
      .select("id, date, mood, mood_score, entry_text, tags")
      .eq("user_id", userId)
      .gte("date", startDate.toISOString().split("T")[0])
      .lte("date", endDate.toISOString().split("T")[0])
      .order("date", { ascending: false })
      .limit(30);

    context.journal_entries = entries || [];
  }

  // Gather meetings with summaries
  if (effectiveSettings.context_meetings) {
    const { data: meetings } = await serviceClient
      .from("meetings")
      .select(`
        id, 
        status, 
        started_at, 
        ended_at,
        calendar_events!inner(title, description, start_time)
      `)
      .eq("user_id", userId)
      .gte("created_at", startDate.toISOString())
      .order("created_at", { ascending: false })
      .limit(20);

    // Also get summaries for these meetings
    if (meetings && meetings.length > 0) {
      const meetingIds = meetings.map(m => m.id);
      const { data: summaries } = await serviceClient
        .from("meeting_summaries")
        .select("meeting_id, summary, key_points, action_items")
        .in("meeting_id", meetingIds)
        .eq("status", "completed");

      // Attach summaries to meetings
      const summaryMap = new Map((summaries || []).map(s => [s.meeting_id, s]));
      context.meetings = meetings.map(m => ({
        ...m,
        summary: summaryMap.get(m.id),
      }));
    } else {
      context.meetings = [];
    }
  }

  // Gather analytics summary
  if (effectiveSettings.context_analytics) {
    // Calculate mood average
    const { data: moodData } = await serviceClient
      .from("journal_entries")
      .select("mood_score")
      .eq("user_id", userId)
      .not("mood_score", "is", null)
      .gte("date", startDate.toISOString().split("T")[0]);

    const moodScores = (moodData || []).map(d => d.mood_score).filter(Boolean);
    const moodAverage = moodScores.length > 0 
      ? moodScores.reduce((a, b) => a + b, 0) / moodScores.length 
      : null;

    // Count events
    const { count: eventCount } = await serviceClient
      .from("calendar_events")
      .select("*", { count: "exact", head: true })
      .eq("user_id", userId)
      .eq("is_deleted", false)
      .gte("start_time", startDate.toISOString());

    // Count meetings
    const { count: meetingCount } = await serviceClient
      .from("meetings")
      .select("*", { count: "exact", head: true })
      .eq("user_id", userId)
      .gte("created_at", startDate.toISOString());

    context.analytics = {
      mood_average: moodAverage || undefined,
      total_events: eventCount || 0,
      total_meetings: meetingCount || 0,
      journal_count: moodScores.length,
    };
  }

  return context;
}

// Build system prompt with context
function buildSystemPrompt(context: UserContext, language: string = "en"): string {
  const languageNames: Record<string, string> = {
    en: "English",
    it: "Italian",
    de: "German",
    fr: "French",
    es: "Spanish",
  };

  let prompt = `You are Desk Mate AI, a helpful productivity assistant. You help users manage their time, understand their meetings, reflect on their journal entries, and gain insights from their productivity data.

Always respond in ${languageNames[language] || "English"}.
Be concise but helpful. Use a friendly, professional tone.
When referencing user data, be specific about dates and details.
If asked about data you don't have, be honest about it.

`;

  if (context.analytics) {
    prompt += `\n## User's Productivity Summary (Last 30 Days)
- Total calendar events: ${context.analytics.total_events}
- Total meetings: ${context.analytics.total_meetings}
- Journal entries: ${context.analytics.journal_count}
${context.analytics.mood_average ? `- Average mood score: ${context.analytics.mood_average.toFixed(1)}/5` : ""}
`;
  }

  if (context.calendar_events && context.calendar_events.length > 0) {
    prompt += `\n## Recent Calendar Events (Last 30 Days)
`;
    context.calendar_events.slice(0, 10).forEach(event => {
      const startDate = new Date(event.start_time).toLocaleDateString();
      prompt += `- ${startDate}: "${event.title}" (${event.event_type})${event.location ? ` at ${event.location}` : ""}\n`;
    });
  }

  if (context.journal_entries && context.journal_entries.length > 0) {
    prompt += `\n## Recent Journal Entries
`;
    context.journal_entries.slice(0, 5).forEach(entry => {
      const mood = entry.mood ? ` - Mood: ${entry.mood}` : "";
      const tags = entry.tags?.length ? ` [${entry.tags.join(", ")}]` : "";
      const preview = entry.entry_text 
        ? entry.entry_text.substring(0, 100) + (entry.entry_text.length > 100 ? "..." : "")
        : "";
      prompt += `- ${entry.date}${mood}${tags}: ${preview}\n`;
    });
  }

  if (context.meetings && context.meetings.length > 0) {
    const completedMeetings = context.meetings.filter(m => m.summary);
    if (completedMeetings.length > 0) {
      prompt += `\n## Recent Meeting Summaries
`;
      completedMeetings.slice(0, 5).forEach(meeting => {
        const title = meeting.calendar_events?.title || "Untitled Meeting";
        prompt += `- "${title}": ${meeting.summary?.summary || "No summary"}\n`;
        if (meeting.summary?.key_points?.length) {
          prompt += `  Key points: ${meeting.summary.key_points.join("; ")}\n`;
        }
      });
    }
  }

  return prompt;
}

// Chat with AI
export async function chatWithAI(
  userId: string,
  conversationId: string,
  userMessage: string,
  context: UserContext,
  settings: AISettings | null
): Promise<{ response: string; contextUsed: object }> {
  const serviceClient = getServiceRoleClient();

  // Get conversation history
  const { data: messages } = await serviceClient
    .from("ai_messages")
    .select("role, content")
    .eq("conversation_id", conversationId)
    .order("created_at", { ascending: true })
    .limit(20);

  const systemPrompt = buildSystemPrompt(context, settings?.preferred_language || "en");

  const chatMessages: OpenAI.Chat.ChatCompletionMessageParam[] = [
    { role: "system", content: systemPrompt },
    ...(messages || []).map(m => ({
      role: m.role as "user" | "assistant",
      content: m.content,
    })),
    { role: "user", content: userMessage },
  ];

  const response = await openai.chat.completions.create({
    model: "gpt-4o-mini",
    messages: chatMessages,
    max_tokens: 1024,
    temperature: 0.7,
  });

  const assistantMessage = response.choices[0].message.content || "I apologize, but I couldn't generate a response.";

  return {
    response: assistantMessage,
    contextUsed: {
      calendar_events_count: context.calendar_events?.length || 0,
      journal_entries_count: context.journal_entries?.length || 0,
      meetings_count: context.meetings?.length || 0,
      has_analytics: !!context.analytics,
    },
  };
}

// Transcribe audio using Whisper
export async function transcribeAudio(
  audioBuffer: Buffer,
  fileName: string
): Promise<{ text: string; duration: number }> {
  // Write buffer to temp file (Whisper API requires a file)
  const tempDir = "/tmp";
  const tempFilePath = path.join(tempDir, `whisper_${Date.now()}_${fileName}`);

  try {
    fs.writeFileSync(tempFilePath, audioBuffer);

    const audioFile = fs.createReadStream(tempFilePath);

    const transcription = await openai.audio.transcriptions.create({
      file: audioFile,
      model: "whisper-1",
      response_format: "verbose_json",
    });

    return {
      text: transcription.text,
      duration: (transcription as any).duration || 0,
    };
  } finally {
    // Clean up temp file
    if (fs.existsSync(tempFilePath)) {
      fs.unlinkSync(tempFilePath);
    }
  }
}

// Generate meeting summary from transcription
export async function generateMeetingSummary(
  transcription: string,
  meetingTitle: string,
  language: string = "en"
): Promise<{
  summary: string;
  key_points: string[];
  action_items: string[];
  decisions: string[];
}> {
  const languageNames: Record<string, string> = {
    en: "English",
    it: "Italian",
    de: "German",
    fr: "French",
    es: "Spanish",
  };

  const prompt = `You are analyzing a meeting transcript. The meeting was titled "${meetingTitle}".

Please provide in ${languageNames[language] || "English"}:
1. A concise summary (2-3 paragraphs)
2. Key points discussed (as a JSON array of strings)
3. Action items identified (as a JSON array of strings)
4. Decisions made (as a JSON array of strings)

Respond with JSON in this exact format:
{
  "summary": "...",
  "key_points": ["point 1", "point 2", ...],
  "action_items": ["action 1", "action 2", ...],
  "decisions": ["decision 1", "decision 2", ...]
}

Meeting Transcript:
${transcription}`;

  const response = await openai.chat.completions.create({
    model: "gpt-4o-mini",
    messages: [{ role: "user", content: prompt }],
    response_format: { type: "json_object" },
    max_tokens: 2048,
    temperature: 0.3,
  });

  const result = JSON.parse(response.choices[0].message.content || "{}");

  return {
    summary: result.summary || "No summary available",
    key_points: result.key_points || [],
    action_items: result.action_items || [],
    decisions: result.decisions || [],
  };
}

